import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import axios from "axios"
class Navbar extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            products:[],
            errmsg:'',
            prodlength:null
        }

    }

    getProducts = () => {
        let dataUrl = `http://127.0.0.1:5000/api/products/`;
    axios.get(dataUrl).then((res) => {
        console.log(res.data)
        this.setState({
            products: res.data,
            prodlength: this.state.products.length
        })  

    }).catch((err) => {
        this.setState({
            errmsg:'error for loading data'
        })
    })
    }

    render(){
        return(
            <Fragment>
                {this.state.prodlength}
                <nav className="navbar navbar-dark nav-color navbar-expand-sm">
                    <div className="container animated slideInLeft">
                        <Link to='/ ' className="navbar-brand text-dark">
                           <i className="fa fa-shopping-cart"/> BigBasket</Link>
                           <div className="collapse navbar-collapse">
                           <ul className="navbar-nav">
                                <li className="nav-item">
                                    <Link to='/home' className="navbar-brand text-dark">Home</Link>
                                </li>
                                <li className="nav-item">
                                    <Link to='/ProductList' className="navbar-brand text-dark">ProductList</Link>
                                </li>
                            </ul>
                           </div>

                        <Link to='/ProductAdmin' className="navbar-brand text-dark">ProductAdmin<i className="fa fa-shopping-cart">{this.props.prodinfo}</i></Link>
                        {/* <Link to='/UpdateProduct' className="navbar-brand d text-dark">UpdateProduct</Link>
                        <Link to='/CreateProduct' className="navbar-brand text-dark">CreateProduct</Link> */}
                    </div>
                </nav>
            </Fragment>
        )
    }
}

export default Navbar
