import React, { Fragment } from "react";

class UpdateProduct extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            productId:''
        }
    }
    componentDidMount(){
        let prodID = this.props.match.params.id;
        this.setState({
            productId : prodID
        })
    }

    render(){
        return(
            <Fragment>
                <div className="container">
                    {this.state.productId}
                </div>
            </Fragment>
        )
    }
}

export default UpdateProduct
