import React, { Fragment } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class ProductAdmin extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            products:[],
            errmsg:''
        }
    }

    componentDidMount(){
    let dataUrl = `http://127.0.0.1:5000/api/products/`;
    axios.get(dataUrl).then((res) => {
        console.log(res.data)
        this.setState({
            products: res.data
        })

    }).catch((err) => {
        this.setState({
            errmsg:'error for loading data'
        })
    })

    }

    render(){
        return(
            <Fragment>
                {}
                 <div className="container mt-4">

                    <div className="row">
                        <div className="col">
                            <p className="h3 text-success ">Products Admin</p>
                            <p className="lead">hsdjs sdhfsjd sdjhfskdf fsjdgfs fshdfgs</p>

                        </div>
                    </div>
                    <div className="row">
                        <div className="col">
                            <table className="table table-hover table-striped text-center table-success">
                                <thead className="bg-dark text-white">
                                    <tr>
                                    <th>s.no</th>
                                    <th>Name</th>
                                    <th>Price</th>
                                    <th>Quantity</th>
                                    <th>Info</th>
                                    <th>Info</th>

                                    </tr>
                                </thead>
                                <tbody>
                                  {
                                    this.state.products ? 
                                    <React.Fragment>
                                        {
                                            this.state.products.map((product) => {
                                                return(
                                                    <tr>
                                                        <td>{product._id.substr(product._id.length - 4)}</td>
                                                        <td>{product.name}</td>
                                                        <td>
                                                            <img src={product.image} alt="image" height="70" width="70"/>
                                                        </td>
                                                        <td>{product.price}</td>
                                                        <td>{product.quantity}</td>
                                                        <td>{product.info}</td>
                                                    </tr>
                                                )
                                            })
                                        }
                                    </React.Fragment> : 'errmsg'
                                  }
                                </tbody>
                            </table>
                            <pre>{JSON.stringify(this.state.products)}</pre>
                            <Link to={'/CreateProduct'} className="btn btn-success btn-sm">Create Product</Link>

                                        <Link to={'/UpdateProduct'} className="btn btn-success btn-sm">Update Product</Link>

                        </div>
                    </div>
                </div>
            </Fragment>
        )
    }
}

export default ProductAdmin
